package game

// import "core:fmt"
import rl "vendor:raylib"


Game :: struct {
	camera: Camera,
	run:    bool,
}

game: ^Game


update :: proc() {
	camera_update()

	if rl.IsKeyPressed(.ESCAPE) {game.run = false}
}

for_each_cell :: proc(origin: [2]i32, size: [2]i32, callback: proc(pos: [2]i32)) {
	for x in origin.x ..< (origin.x + size.x) {
		for y in origin.y ..< (origin.y + size.y) {
			callback({x, y})
		}
	}
}

render :: proc() {
	rl.BeginDrawing()
	rl.ClearBackground(rl.BLACK)

	rl.BeginMode2D(make_rl_camera())


	// TEMP
	m := make(map[[2]i32]i32)
	defer delete(m)
	m[{1, -1}] = -1
	m[{0, 0}] = 1
	m[{1, 1}] = 2
	m[{2, 2}] = 3

	center: [2]i32 = {i32(game.camera.pos.x), i32(game.camera.pos.y)}
	render_distance: i32 = 4

	for_each_cell(center - render_distance, {render_distance, render_distance}, proc(pos: [2]i32) {
		pos_float: [2]f32 = {f32(pos.x), f32(pos.y)}

		rl.DrawRectangleLines(pos.x, pos.y, 1, 1, rl.DARKGRAY)

		if m[pos] != 0 {
			rl.DrawRectangleV(pos_float + {0.05, 0.05}, {0.9, 0.9}, rl.GRAY)
		}
	})


	// TEMP


	rl.EndMode2D()

	ui_render()

	rl.EndDrawing()
}

@(export)
game_update :: proc() {
	update()
	render()
}

@(export)
game_init_window :: proc() {
	rl.SetConfigFlags({.WINDOW_RESIZABLE, .VSYNC_HINT})
	rl.InitWindow(1280, 720, "Plasma Planet")
	rl.SetWindowPosition(200, 200)
	rl.SetTargetFPS(500)
	rl.SetExitKey(nil)
}

@(export)
game_init :: proc() {
	game = new(Game)

	game^ = Game {
		camera = camera_init(),
		run    = true,
	}

	game_hot_reloaded(game)
}

@(export)
game_should_run :: proc() -> bool {
	when ODIN_OS != .JS {
		// Never run this proc in browser. It contains a 16 ms sleep on web!
		if rl.WindowShouldClose() {
			return false
		}
	}

	return game.run
}

@(export)
game_shutdown :: proc() {
	free(game)
}

@(export)
game_shutdown_window :: proc() {
	rl.CloseWindow()
}

@(export)
game_memory :: proc() -> rawptr {
	return game
}

@(export)
game_memory_size :: proc() -> int {
	return size_of(Game)
}

@(export)
game_hot_reloaded :: proc(mem: rawptr) {
	game = (^Game)(mem)

	// Here you can also set your own global variables. A good idea is to make
	// your global variables into pointers that point to something inside
	// `g_mem`.
}

@(export)
game_force_reload :: proc() -> bool {
	return rl.IsKeyPressed(.F5)
}

@(export)
game_force_restart :: proc() -> bool {
	return rl.IsKeyPressed(.F6)
}

